import React from 'react';

class Image extends React.Component {

    constructor(props) {
        super(props);
        this.image = props.background;
    }

    render() {
        let sectionClasses = 'nxt-image-section';
        
        if (this.props.hero) {
            sectionClasses = sectionClasses + ' nxt-image-section--hero';
        }

        sectionClasses += ' nxt-image-section--' + this.props.side;
        
        return (
            <section class={sectionClasses}>
                <figure class='nxt-image-section__image'>
                    <img src={this.props.background} />
                </figure>
                <div class='nxt-image-section__text'>
                    <h2 className='nxt-image-section__head'>{this.props.mainText}</h2>
                </div>
            </section>
        );
    }

}

export default Image;
